mapper.insert:
/* todoDetail.insert */

	SELECT MAX(SEQ) + 1 FROM TB_TODO_DETAIL

INSERT INTO 
	TB_TODO_DETAIL(
		PARENT_SEQ
		, DATE
		, CONTENTS
		, FILE_SEQS
		, USE_YN
	)VALUES(
		#{parentSeq}
		, #{date}
		, #{contents}
		, #{fileSeqs}
		, 'Y'
	)

mapper.update:
/* todoDetail.update */
UPDATE 
	TB_TODO_DETAIL 
SET 
	DATE = #{date}
	, CONTENTS = #{contents}
	, FILE_SEQS = #{fileSeqs}
	, USE_YN = #{useYn}
WHERE 
	SEQ = #{seq}
